public void drawLine(int left, float y, int right, float y2,
			RSPaint gridPaint) {

		SwingPaint gp = (SwingPaint) gridPaint;

		g.setColor(gp.color);
		g.setStroke(gp.stroke);

		g.drawLine (left, (int)y, right, (int)y2);
	}
