@Override
    public String execute(String content) {
        if (StringUtils.isBlank(content)) {
            return content;
        }
        String s = super.execute(content);
        try {
            s = URLDecoder.decode(s, "UTF-8");
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
        return s;
    }
