@Override
    public String execute(String content) {
        if (StringUtils.isBlank(content)) {
            return content;
        }
        try {
            return Normalizer.normalize(content, Normalizer.Form.NFC);
        } catch (Exception e) {
            throw new IllegalArgumentException("Canonicalization error", e);
        }
    }
