@Override
    public void preparacao(final Parser parser) {
        if (parser == null) {
            throw new CampoExigidoNaoFornecido("parser");
        }

        List<String> dd = parser.dependencias(expressao);
        setDependeDe(dd);

        ctx = new HashMap<>(dd.size());
        for (String dep : dd) {
           ctx.put(dep, 0f);
        }

        ast = parser.ast(expressao);
    }
