@NotNull
  public static String resolve(@NotNull String basePath, @NotNull String other) {
    if (isEmpty(other)) {
      return basePath;
    }
    if (isAbsolute(other)) {
      return other;
    }
    if (isSuffixed(basePath)) {
      return basePath + other;
    }

    return basePath + FILE_SEPARATOR + other;
  }
