@Nullable
  public static Container setCursorOnProfilerLayeredPane(Container container, Cursor cursor) {
    for (Container p = container; p != null; p = p.getParent()) {
      if (p instanceof ProfilerLayeredPane) {
        p.setCursor(cursor);
        return p;
      }
    }
    return null;
  }
