@Test
    public void testThatQualityOfItemDecreasesByTwoIfSellDatePassed() {
        Item item = new Item("An item", 0, 50);
        GildedRose.updateQualityOf(item);
        assertThat(item.getQuality()).isEqualTo(48);
    }

