public int[] transformPoint(double xCoord, double yCoord) {
        double[] src = new double[]{xCoord, yCoord};
        double[] dest = new double[2];
        this.transform.transform(src, 0, dest, 0, 1);
        return new int[]{(int) dest[0], (int) dest[1]};
    }
