public DashboardItem getAvailableItemByType( DashboardItemType type )
    {
        for ( DashboardItem item : items )
        {
            if ( type.equals( item.getType() ) && item.getContentCount() < DashboardItem.MAX_CONTENT )
            {
                return item;
            }
        }

        return null;
    }
