        @Override
        public Histogram call(Histogram initialDistribution, HystrixCollapserEvent event) {
            switch (event.getEventType()) {
                case ADDED_TO_BATCH:
                    if (event.getCount() > -1) {
                        initialDistribution.recordValue(event.getCount());
                    }
                    break;
                default:
                    //do nothing
                    break;
            }
            return initialDistribution;
        }
    };

