@Test
    public void serializeTest() {
        Center center = new Center();
        center.setId(7);
        center.setX(12.3);
        center.setY(4.56);
        JsonElement serialized = centerAdapter.serialize(center, Center.class, null);
        assertThat(serialized.toString(), is(equalTo("[12.3,4.56]")));
    }

