    public void deriveMemberNameFrom_with_dollar() {
        final String suffix = ObjectMemberAbstract.deriveMemberNameFrom("Customer$placeOrder");
        Assert.assertThat(suffix, CoreMatchers.is(equalTo("placeOrder")));
    }

