@Test
    public void testEscape() throws Exception {

        assertThat(JGrowlUtil.escape(
                "double quotes \" and single quotes ' and <p>markup</p>"), equalTo(
                        "double quotes ' and single quotes ' and &lt;p&gt;markup&lt;/p&gt;"));
    }

