@Override
	public void pause() {
		for (Entry<PhysicalPartition, EventProducer> entry : _producers
				.entrySet()) {
			EventProducer producer = entry.getValue();

			if (null != producer) {
				if (producer.isRunning()) {
					producer.pause();
					LOG.info("EventProducer :" + producer.getName()
							+ "  pause sent");
				} else if (producer.isPaused()) {
					LOG.info("EventProducer :" + producer.getName()
							+ "  already paused");
				}
			}
		}
	}
