public static Class loadClass(String className)
    throws Exception
    {
    	try
    	{
    	  Class cName = OracleJarUtils.class.getClassLoader().loadClass(className);
  		  return cName;    		
    	} catch (Exception e)
    	{
    		LOG.error("Error loading a class " + className + " from ojdbc jar", e);
    		throw e;
    	}
    }
