@Api(2.0)
    @Nullable
    public static Class<?> toClass(@Nullable String name) {
        if (!TextUtils.isEmpty(name)) {
            try {
                return Class.forName(classNameToInnerFilter(name.trim()));
            } catch (ClassNotFoundException ignored) {
            }
        }
        return null;
    }
