@Override
    public String toString() {
        String r = "";
        try {
            Object[] a = super.getAttributes();

            for (int i = 0; i < a.length; i++) {
                if (i > 0)
                    r = r + ",";
                r = r + a[i].toString();
            }
        } catch (SQLException e) {
            System.out.println("TeradataDatePeriod: toString: caught exception");
        }

        return r;
    }
