@Test
    public void testParseInt() {
        assertEquals(0, ZTSUtils.parseInt(null, 0));
        assertEquals(-1, ZTSUtils.parseInt("", -1));
        assertEquals(100, ZTSUtils.parseInt("100", 1));
        assertEquals(0, ZTSUtils.parseInt("abc", 0));
    }

