public static void deleteDirectoryRecursively(File file) {
        File[] contents = file.listFiles();
        if (contents != null) {
            for (File f : contents) {
                deleteDirectoryRecursively(f);
            }
        }
        boolean deleted = file.delete();
        if (deleted) {
            log.debug("File {} was deleted", file.getAbsolutePath());
        }
    }
