    public void testConvertShouldThrowIllegalArgumentExceptionIfVarargsIsTrue() {
        // Given:
        Object data = 3.0;
        Class<?>[] parameterTypes = new Class<?>[] { double.class };

        // When:
        underTest.convert(data, true, parameterTypes);

        // Then: expect exception
    }

