@Test
    public void tryToCreateInstanceWithNullPolicyId() {
        assertThatExceptionOfType(NullPointerException.class)
                .isThrownBy(() -> RetrievePolicy.of((PolicyId) null, EMPTY_DITTO_HEADERS))
                .withNoCause();
    }

