@Override
    public void success(final ConnectionMonitor.InfoProvider infoProvider) {
        if (active) {
            delegate.success(infoProvider);
        } else {
            logTraceWithCorrelationId("Not logging success since logger is muted.", infoProvider);
        }
    }
