@Test
    public void getInstanceWithNullLogger() {
        assertThatNullPointerException()
                .isThrownBy(() -> DefaultAutoCloseableSlf4jLogger.of(null))
                .withMessage("The logger must not be null!")
                .withNoCause();
    }

