static JsonExceptionBuilder<JsonPointerInvalidException> newBuilderForConsecutiveSlashes(
            final CharSequence jsonPointer) {
        return new Builder()
                .jsonPointer(jsonPointer)
                .description(MULTIPLE_SLASHES_DESCRIPTION);
    }
