@Override
	public double distance(final double[] a, final double[] b) {
		// Parameter checks.
		RobustnessUtil.sameLengthOrDie(a, b);

		int n = a.length;
		// Sum over all elements.
		double result = 0;
		for (int i = 0; i < n; i++) {
			result += Math.abs(a[i] - b[i]);
		}

		return result;
	}
